import React, { Component } from 'react';
class Header extends Component{

    scrollTo = (name) => {
        scrollToSection(document.getElementById(name));
    }

    scrollToSection = (elem) => {
        var jump = parseInt(elem.getBoundingClientRect().top * .2);
        document.body.scrollTop += jump;
        document.documentElement.scrollTop += jump;
        if (!elem.lastjump || elem.lastjump > Math.abs(jump)) {
          elem.lastjump = Math.abs(jump);
          setTimeout(function() { ScrollToResolver(elem);}, "100");
        } else {
          elem.lastjump = null;
        }
    }

    // function ScrollTo(name) {
    //     ScrollToResolver(document.getElementById(name));
    //   }

    //   function ScrollToResolver(elem) {
    //     var jump = parseInt(elem.getBoundingClientRect().top * .2);
    //     document.body.scrollTop += jump;
    //     document.documentElement.scrollTop += jump;
    //     if (!elem.lastjump || elem.lastjump > Math.abs(jump)) {
    //       elem.lastjump = Math.abs(jump);
    //       setTimeout(function() { ScrollToResolver(elem);}, "100");
    //     } else {
    //       elem.lastjump = null;
    //     }
    //   }


    render(){

        const{ navigation } = this.props;
        const headerList = navigation.map(navItem => {
            if (navItem.name === "header"){
            //    for (let key in navItems.links) {
            //         if (navItems.links.hasOwnProperty(key)){
               return(
                    <ul key={navItem.id}>
                        <li><a href="#about">about</a></li>
                        <li><a href='#projects'>projects</a></li>
                        <li><a href="https://ceciceciceci.github.io/artindex.html">art</a></li>
                        <li><a href="/contact">contact</a></li>
                    </ul>
               )
            }
        })
        return (
            <header>
                <nav>
                    <a className="logo" href="/">
                        <svg width="169" height="141" viewBox="0 0 169 141" fill="none" xmlns="http://www.w3.org/2000/svg">

                            <g mask="url(#mask0)">
                                <ellipse cx="84.5" cy="70.5" rx="84.5" ry="70.5" fill="#C46D24"/>
                                <line x1="37.1405" y1="19.6057" x2="103.606" y2="55.8595" stroke="#88390C" stroke-width="12" stroke-linecap="round" stroke-linejoin="round"/>
                                <line x1="62.1405" y1="3.60574" x2="128.606" y2="39.8595" stroke="#88390C" stroke-width="12" stroke-linecap="round" stroke-linejoin="round"/>
                                <line x1="103.816" y1="2.12539" x2="37.1254" y2="37.9638" stroke="#88390C" stroke-width="12" stroke-linecap="round" stroke-linejoin="round"/>
                                <line x1="128.816" y1="23.1254" x2="62.1254" y2="58.9638" stroke="#88390C" stroke-width="12" stroke-linecap="round" stroke-linejoin="round"/>
                            </g>
                        </svg>
                    </a>

                    {/* <a className="logo" href="/"><img src={require('../imgs/breadlogo.png')} alt="Logo" /></a> */}
                    { headerList } 
                </nav>
            </header>
        )
    }
}

export default Header;
